<?php/** * Created by PhpStorm. * User: janhalozan * Date: 4/15/14 * Time: 2:37 PM * * Route: / * Model: <none - yet> * * */class IndexController extends BaseController {    //This is the default action    public function index()     {        //Get the user location        $place = 'Maribor';        //Populate our view with data for the current city        $view = View::make('index');        // TEMPERATURE        $record = CurrentWeather::first()->temperature;        $view->temperature = round($record);        // FACT        try        {            $randomFact = rand(0, 127);            $lastFactId = Facts::orderBy('id', 'desc')->first()->id;            $view->fact = Facts::find($lastFactId - $randomFact)->fact;        }        catch (Exception $e)        {            $view->fact = "That our random fact feature is not working.";        }        return $view;    }}